int n, a[200005], b[200005], ans;

inline bool check(int x) {
	for (int i = 1; i <= n; i++) b[i] = (a[i] >= x);
	int res = -1, mn = 0x7fffffff;
	for (int i = 1; i <= n / 2; i++) {
		if (b[i] == b[i+1]) {
			int dis = (n/2+1) - (i+1);
			if (dis < mn) {
				mn = dis; res = b[i];
			}
		}
	}
	for (int i = n / 2 + 1; i <= n - 1; i++) {
		if (b[i] == b[i+1]) {
			int dis = (n/2+1) - i;
			if (dis < mn) {
				mn = dis; res = b[i];
			}
		}
	}
	return (~res ? res : b[1]);
}

inline void solve() {
	int l = 1, r = n, mid;
	while (l <= r) {
		mid = (l + r) >> 1;
		if (check(mid)) {
			ans = mid; l = mid + 1;
		} else r = mid - 1;
	}
}

