	if (l > 1 && (a[l] > x) == (a[l - 1] > x))
		return a[l] > x;
	if (l < m && (a[l] > x) == (a[l + 1] > x))
		return a[l] > x;
	return -1;
}

inline bool check(int x)
{
	if (!is_pillar(n, x))
		return true;

	int pl = -1, vl = -1;
	for (int i = n; !~vl && i >= 1; --i)
		if (~(vl = is_pillar(i, x)))
			pl = i;

	int pr = -1, vr = -1;
	for (int i = n; !~vr && i <= m; ++i)
		if (~(vr = is_pillar(i, x)))
			pr = i;

	if (~vl && ~vr)
	{
		if (!vl && !vr)
			return true;

		int dl = n - pl, dr = pr - n;
		if (vl == 1)
			swap(dl, dr);
		if (!vl && dl < dr)
			return true;
	}
	else
	{
		if (!vl || !vr)
			return true;

		int w = 0, b = 0;
		for (int i = 1; i <= m; ++i)
			a[i] <= x ? ++w : ++b;
		if (w > b)
			return true;
	}

	return false;
}

