int med(int a,int b,int c){
	return (a+b+c)-(max(max(a,b),c))-(min(min(a,b),c));
}
int main(){
	//freopen("input.txt","r",stdin);
	//freopen("output.txt","w",stdout);
	ios::sync_with_stdio(false);
	cin.tie(NULL);
	cout.tie(NULL);
	cin>>n;
	int x;
	for(int i=1;i<=2*n-1;i++)
	{
		cin>>x;
		v.push_back(x);
	}
	upd.resize(2*n-1);
	for(int i=0;i<n-1;i++){
		for(int j=n-1;i+1<=j;j--){
			upd[j]=med(v[j-1],v[j],v[j+1]);
			if(upd[j]==upd[j+1])
				break;
		}
		for(int j=n-1;j<(2*n-1-i-1);j++){
			upd[j]=med(v[j-1],v[j],v[j+1]);
			if(upd[j]==upd[j-1])
				break;
		}
		v.swap(upd);
	}
	cout<<v[n-1]<<endl;
	return 0;
}

