vector<int> cer;
bool ok(int val){
	ar.clear();
	for (int i = 0; i < v.size(); i++){
		ar.push_back(v[i] >= val);
	}
	cer.assign(ar.size(), 0);
	for (int i = 0; i < ar.size(); i++){
		if (ar[i]){
			if (i&&ar[i - 1]){
				cer[i] = ar[i];
			}
			if (i + 1 < ar.size() && ar[i + 1]){
				cer[i] = ar[i];
			}
		}
	}
	int u = n-1;
	if (cer[u]){
		return true;
	}
	int lef = 0;
	for (int i = 0; i < u; i++){
		if (cer[i]){
			lef = i + 1;
		}
	}
	int rig = ar.size() - 1;
	for (int i = ar.size() - 1; i > u; i--){
		if (cer[i]){
			rig = i-1;
		}
	}
	int L, R;
	L = u-1;
	R = u+1;
	if (n % 2 == 0){
		for (int i = 0; i < n/2; i++){
			if (L >= lef){
				cer[L] = true;
			}
			if (R <= rig){
				cer[R] = true;
			}
			L -= 2;
			R += 2;
		}
		L += 2;
		R -= 2;
	}
	else{
		L = R = u;
		for (int i = 0; i <= n / 2; i++){
			if (L >= lef){
				cer[L] = true;
			}
			if (R <= rig){
				cer[R] = true;
			}
			L -= 2;
			R += 2;
		}
		L += 2;
		R -= 2;
	}
	L = max(L, lef);
	R = min(R, rig);
	for (int i = L; i <= R; i++){
		if (cer[i] != ar[i])return false;
	}
	return true;
}

