bool valid(int mid)
{   
    for(int i = 0; i < n-1; ++i)
        if(max(p[n-i], p[n-i-1]) <= mid||max(p[n+i], p[n+i+1]) <= mid) return true;
        else if(min(p[n-i], p[n-i-1]) > mid||min(p[n+i], p[n+i+1]) > mid) return false;
    return p[1] <= mid;
}
