bool pass(int mid)
{
	int i,l=0,r=2*n;;
	for (i=1;i<2*n;i++)
		x[i]=a[i]<=mid;
	for (i=2*n-1;i>n;i--)
		if (x[i]==x[i-1]) r=i;
	for (i=1;i<n;i++)
		if (x[i]==x[i+1]) l=i;
	if (n-l<r-n) return x[l];
	if (n-l>r-n) return x[r];
	if (l!=0&&x[l]==x[r]) return x[l];
	return !x[n]^(n&1);
}

