int work(){
	if(b[n]==b[n-1]||b[n]==b[n+1]) return b[n];
	int g=n-1,f=n+1;
	while(g){
		if(b[g]==b[g-1]) break;
		g--;
	}
	while(f<=2*n-1){
		if(b[f]==b[f+1]) break;
		f++;
