		b[i] = (a[i] >= piv);
	}
	int A = inf, B = inf;
	for(int i=1;n-i>=1;i++) {
		if(b[n-i] == b[n-i+1]) {A = i; break;}
	}
	for(int i=1;n+i<=2*n-1;i++) {
