bool bin(int x) {
  for (int i = 0; i < m; ++i)
    b[i] = a[i] <= x ? 1 : 0;

  int mid = n - 1;
  int l = mid, r = mid;
  int dl = 0, dr = 0;

  while (l > 0 && b[l - 1] != b[l]) {
    --l;
    ++dl;
  }

  while (r + 1 < m && b[r] != b[r + 1]) {
    ++r;
    ++dr;
  }

  if (l == 0 && r == m - 1) {
    return ((n - 1) % 2) ^ b[mid];
  }

  if (dl < dr) 
    return b[l];
  else
    return b[r];
}

