const int MAXN = 2e5 + 5;

int N;
int A[MAXN];

void input()
{
    read(N);
    for (int i = 1; i < N * 2; ++i) {
        read(A[i]);
    }
}

int get(int x)
{
    static int B[MAXN];

    for (int i = 1; i < N * 2; ++i) {
        B[i] = (A[i] >= x);
    }
    for (int i = 1; i < N; ++i) {
        if (B[N - i + 1] == B[N - i])
            return B[N - i];
        if (B[N + i - 1] == B[N + i])
            return B[N + i];
    }
    return B[N] ^ (~N & 1);
}

void solve()
{
    int l = 1, r = N * 2;
    while (l + 1 < r) {
        int mid = (l + r) >> 1;
        int t = get(mid);
        if (t == 0)
            r = mid;
        else
            l = mid;
    }

    printf("%d\n", l);
}

