// -------------------- Spliters ------------------------
const int maxn = 200050;

int a[maxn];

int n;
bool judge(int x) {
	int last = -1;
	int nearest = 9999999;
	int zo = 0;
	int mid = n / 2;
	rep(i, 0, n) {
		int cur = x <= a[i] ? 1 : 0;
		if(cur == last) {
			if(nearest > abs(i - mid)) {
				nearest = abs(i - mid);
				zo = cur;
			}
		}
		last = cur;
	}
	last = -1;
	for(int i = n - 1; i >= 0; i--) {
		int cur = x <= a[i] ? 1 : 0;
		if(cur == last) {
			if(nearest > abs(i - mid)) {
				nearest = abs(i - mid);
				zo = cur;
			}
		}
		last = cur;
	}
	if(nearest == 9999999) {
		return a[0] >= x;
	}
	return zo == 1;
}

int main() {
	ios::sync_with_stdio(0);
	cin>>n;
	n = 2 * n - 1;
	rep(i, 0, n) cin>>a[i];
	if(n == 1) {
		cout<<1<<endl;
		return 0;
	}
	int l = 2, r = n - 1;
	int ans = -1;
	while(l < r) {
		int mid = (l + r) >> 1;
		if(judge(mid)) {
			l = mid + 1;
			ans = mid;
		} else {
			r = mid - 1;
		}
	}
	for(int i = l - 1; i <= r + 1; i++) {
		if(judge(i)) ans = max(ans, i);
	}
	cout<<ans<<endl;
	return 0;
}
