inline li med(li idx) {
    li vals[3];
    rep(i, 3)vals[i] = a[idx + i];
    sort(vals, vals + 3);
    return vals[1];
}

inline bool check(li x) {
    rep(i, 2 * n - 3) {
        tmp[i] = (med(i) >= x ? 1 : 0);
    }
    if (tmp[n - 2] == tmp[n - 1] || tmp[n - 2] == tmp[n - 3]) {
        return tmp[n - 2] == 1;
    }
    // center : n-2
    PI right;
    repp(i, n - 2, 2 * n - 3) {
        if (i == 2 * n - 4) {
            right.F = -1;
            break;
        }
        if (tmp[i] == tmp[i + 1]) {
            right.F = i - n - 2;
            right.S = tmp[i];
            break;
        }
    }

    PI left;
    repr(i, n - 3) {
        if (i == 0) {
            left.F = -1;
            break;
        }
        if (tmp[i] == tmp[i - 1]) {
            left.F = n - 2 - i;
            left.S - tmp[i];
        }
    }
    if (left.F < 0 && right.F < 0) {
        return 0;
    }
    if (left.F < 0) {
        return (right.S == 1);
    }
    if (right.F < 0) {
        return (left.S == 1);
    }
    if (left.F < right.F) {
        return (left.S == 1);
    }
    if (left.F > right.F) {
        return (right.S == 1);
    }
    // (n-2)%2
    if (n % 2 > 0) {
        return tmp[n - 2] == 1;
    } else {
        return tmp[n - 2] == 0;
    }
}

