bool big(int p, int q, int c)
{
	return (a[p] >= c && a[q] >= c);
}

bool small(int p, int q, int c)
{
	return (a[p] < c && a[q] < c);
}

bool chk(int x)
{
	for(int i = n, j = n; i >= 2 && j <= 2 * n - 2; i --, j ++)
	{
		if(big(i, i - 1, x) || big(j, j + 1, x))
		{
			return 1;
		}
		if(small(j, j + 1, x) || small(i, i - 1, x))
		{
			return 0;
		}
	}
	return 0;
}

