int median(int a, int b, int c) {
    if (a > b) swap(a, b);
    return max(a, min(b, c));
}
int main() {
    // input
    int n; scanf("%d", &n);
    vector<int> a(2 * n - 1);
    repeat (i, 2 * n - 1) {
        scanf("%d", &a[i]);
    }
    // solve
    vector<int> b(2 * n - 1);
    repeat (i, n - 1) {
        for (int j = n - 1; i + 1 <= j; -- j) {
            b[j] = median(a[j - 1], a[j], a[j + 1]);
            if (b[j] == b[j + 1]) break;
        }
        for (int j = n - 1; j < (2 * n - 1) - (i + 1); ++ j) {
            b[j] = median(a[j - 1], a[j], a[j + 1]);
            if (b[j - 1] == b[j]) break;
        }
        a.swap(b);
    }
    // output
    printf("%d\n", a[n - 1]);
    return 0;
}

