const int N=5e5+19;

int A[N],B[N];
int n,l,r,res;

int check(int mid){
	int x=(n+1)/2,pre=-1,suf=-1;
	For(i,1,n+1) B[i]=(A[i]>=mid);
	if (B[x]==B[x-1]||B[x]==B[x+1]) return B[x]==1;
	for (int i=x-1;i>=1;i--) if (B[i]==B[i+1]) {pre=i;break;}
	for (int i=x+1;i<=n;i++) if (B[i]==B[i-1]) {suf=i;break;}
	if (pre==-1&&suf==-1) return (B[x]+x-1)%2;
	if (pre==-1) return B[suf];
	if (suf==-1) return B[pre];
	return x-pre<suf-x?B[pre]:B[suf];
}

