const int MAXN = 100005;

int N;
int a[2 * MAXN];
int tmp[2 * MAXN];

void load() {
  scanf("%d", &N);
  for (int i = 1; i < 2 * N; i++)
    scanf("%d", a + i);
}

bool check(int val) {
  for (int i = 1; i < 2 * N; i++)
    tmp[i] = a[i] >= val;

  for (int i = 1; i < N; i++) {
    if (tmp[N - i] == tmp[N - i + 1])
      return tmp[N - i];
    if (tmp[N + i] == tmp[N + i - 1])
      return tmp[N + i];
  }

  return tmp[N] ^ ((N - 1) % 2);
}

int solve() {
  int lo = 1, hi = 2 * N - 1;
  while (lo < hi) {
    int mid = (lo + hi + 1) / 2;
    if (check(mid))
      lo = mid;
    else
      hi = mid - 1;
  }

  return lo;  
}

int main() {
  load();
  printf("%d\n", solve());
  return 0;
}

