int N,M,a[200000];
bool can(int m){
	int p=2;
	int L,R;
	bool l=0,r=0;
	for(L=N-1;L>=0;L--){
		bool b=(a[L]<=m);
		if(p==b){
			l=p;
			break;
		}
		p=b;
		if(L==0){
			if(b) L=-2;
			else L=-1;
			break;
		}
	}
	p=2;
	for(R=N-1;R<M;R++){
		bool b=(a[R]<=m);
		if(p==b){
			r=p;
			break;
		}
		p=b;
		if(R==M-1){
			if(b) R=M+1;
			else R=M;
			break;
		}
	}
	if(!l && !r){
		if(L==-2 && R==M+1) return 1;
		return 0;
	}
	if(l^r){
		return (((N-1)-L)>(R-(N-1)))^l;
	}
	return 1;
}
