	M=N*2-1;
	rep(i,M) cin>>a[i];
	int ub=M,lb=0;
	while(ub-lb>1){
		int m=(ub+lb)/2;
		if(can(m)) ub=m;
		else lb=m;
	}
	cout<<ub<<endl;
}

