bool solve(int x){
	for(int i=1;i<=m;i++) b[i]=(a[i]>=x);
	int tot=0;
	for(int i=1;i<m;i++){
		if(b[i]!=b[i+1]) continue;
		tot++;c[tot].l=i;c[tot].w=b[i];
		while(b[i]==b[i+1]&&i<m) i++;
		c[tot].r=i;
	}
	if(!tot) return b[1];
	c[++tot].l=m+1;c[tot].r=1e9;
	for(int i=1;i<=tot;i++){
		int l=c[i-1].r+1,r=c[i].l-1;
		if(l>r) continue;
		int mid=l+r>>1;
		c[i-1].r=mid;c[i].l=mid+1;
	}
	for(int i=1;i<=tot;i++) if(c[i].l<=n&&c[i].r>=n) return c[i].w;
}
