    top=0;
    for(int i=1;i<=2*n-1;i++)
    {
        if(f[i]!=f[i-1]&&f[i]!=f[i+1])  s[++top]=i;
    }
    for(int i=1;i<=top;i++)
    {
        int l=i,r=i;
        while(s[r+1]==s[r]+1&&r<top)  r++;
        i=r;
