int getMid(int x1,int x2,int x3)
{
	if (x1==x2 && x1==x3){
		return x1;
	}
	if (x1==x2 || x1==x3 || x2==x3){
		return min(x1,min(x2,x3));
	}
	int xMax=max(x1,max(x2,x3));
	int xMin=min(x1,min(x2,x3));
	if (x1 != xMax && x1!=xMin){
		return x1;
	}
	if (x2 != xMax && x2!=xMin){
		return x2;
	}
	if (x3 != xMax && x3!=xMin){
		return x3;
	}
	return -1;
}
