// warning : there's get and sum method.
/// --- BIT Library {{{ ///

struct BIT{
  vector<long long> data;
  int n;
  BIT(int n): n(n){
    data.resize(n, 0);
  }
  void clear(int sz=0) {
    if(sz > 0) n = sz;
    data.assign(n, 0);
  }
  void add(int i, long long x) {
    i++;
    while(i<=n){
      data[i-1] += x;
      i += i&-i;
    }
  }
  void set(int i, long long x) {
    add(i, x - get(i));
  }
  long long sum(int i) {
    assert(i < n);
    if(i<0) return 0;
    i++;
    long long s = 0;
    while(i>0){
      s += data[i-1];
      i -= i&-i;
    }
    return s;
  }
  long long get(int i) {
    return sum(i) - (i ? sum(i - 1) : 0);
  }
  long long range(int a, int b) {
    return sum(b) - sum(a-1);
  }
};

/// }}}--- ///

const int N = 2e5;
BIT bit(N);
int n, m;
int a[N];
bool low[N];
bool is(int l, int r) {
  if(r < l) swap(l, r);
  if(r >= n) return 0;
  return bit.range(l + 1, r) == r - l;
}

void check(int i) {
  if(i < 0 || i >= n) return;
  bit.set(i, low[i] != low[i - 1]);
}

template<class T> inline int sign(T x) { return x > 0 ? 1 : x < 0 ? -1 : 0; }

int main() {
  ios::sync_with_stdio(false), cin.tie(0);
  cin >> m;
  n = m + m - 1;
  int c = m - 1;
  for(int i = 0; i < n; i++) cin >> a[i], a[i]--;
  vector<int> ord(n);
  iota(begin(ord), end(ord), 0);
  sort(begin(ord), end(ord), [&](int x, int y) {
    return a[x] < a[y];
  });
  for(int id = 0; id < n; id++) {
    int i = ord[id];
    int sg = sign(c - i);
    int d = abs(c - i);
    int aok = 0;
    if(d == 0) {
      dump(i, 1);
      if(is(c - 2, c - 1) && low[c - 1] != low[c + 1]) aok = 1;
      if(is(c + 1, c + 2) && low[c - 1] != low[c + 1]) aok = 1;
    } else {
      dump(i, 2);
      if(is(i + sg * 2 * d, i + sg) && (
        i > 0 && i < n - 1 ?
        low[i + 1] != low[i - 1] :
        1
        )) aok = 1;
      if(i > 1 && i < n - 2 && is(i + sg, i + sg * d * 2) && low[i - 1] == low[i + 1]) {
        int ok = 0, ng;
        ng = min(i, n - 1 - i) / 2 - 1 + 1;
        while(ng - ok > 1) {
          int mid = (ng + ok) >> 1;
          if(is(i + sg, i + sg * d * 2 + sg * 2 * mid)) ok = mid;
          else ng = mid;
        }
        int x = i + sg * d * 2 + sg * 2 * ok;
        int y = i - sg * 2 * (ok + 1);
        if(is(i - sg, i - sg * 2 * (ok + 1)) && (
          low[x] == low[x + sg] || (
            y - sg >= 0 && y - sg < n && low[y] == low[y - sg]
          )
          )) aok = 1;
      }
    }
    if(i % 2 == 0 && i > 0 && i < n - 1 && is(0, i - 1) && is(i + 1, n - 1) && low[i - 1] == low[i + 1]) aok = 1;
    if(aok) return (cout << id + 1 << endl, 0);
    low[i] = 1;
    check(i);
    check(i+1);
  }
  cout << m << endl;
  /* assert(0); */
}

