bool provjeri(){
	if(B(n - 1) == B(n))
		return B(n - 1);
	if(B(n - 1) == B(n - 2))
		return B(n - 1);
	int l = n - 1, r = n - 1;
	while(l > 0 && B(l) != B(l - 1))
		l--;
	while(r < 2 * n - 2 && B(r) != B(r + 1))
		r++;
	if(l == 0)
		return B(r);
	if(r == 2 * n - 2)
		return B(l);
	if((n - 1) - l < r - (n - 1))
		return B(l);
	return B(r);
}

