bool solve(const vector<int>& a, int x)
{
    int n = (a.size() + 1) / 2;
    for(int i=0; i<n-1; ++i){
        if((a[n-1-i] >= x) == (a[n-2-i] >= x))
            return a[n-1-i] >= x;
        if((a[n-1-i] >= x) == (a[n+i] >= x))
            return a[n-1-i] >= x;
    }
    return (a[n-1] >= x) ^ (n % 2 == 0);
}

int main()
{
    int n;
    cin >> n;
    vector<int> a(2*n-1);
    for(int i=0; i<2*n-1; ++i)
        cin >> a[i];

    int left = 1;
    int right = 2 * n - 1;
    while(left < right){
        int mid = (left + right + 1) / 2;
        if(solve(a, mid))
            left = mid;
        else
            right = mid - 1;
    }
    cout << left << endl;

    return 0;
}

