int S(int i, int j, int k) {return a[i] <= k && a[j] <= k;}
int M(int i, int j, int k) {return a[i] > k && a[j] > k;}
int check(int k) {
	for (int i = 0; i < n - 1; ++i) {
		if(M(n + i, n + i + 1, k) || M(n - i, n - i - 1, k)) return 0;
		if(S(n + i, n + i + 1, k) || S(n - i, n - i - 1, k)) return 1;
	}
	return S(1, 1, k);
} 
