class Solve {
public:
    int n, N;
    vector<int> a;

    void input() {
        cin >> n;
        N = 2 * n - 1;
        a.resize(N);
        rep(i, 0, N) cin >> a[i];

    }

    bool check(int k) {
        vector<int> b(N);
        rep(i, 0, N) b[i] = a[i] >= k;

        int mid_p = N / 2;
        if (b[mid_p] == b[mid_p - 1] || b[mid_p] == b[mid_p + 1]) {
            return b[mid_p];
        }

        int l = mid_p;
        int r = mid_p;
        while (l > 0 && b[l] ^ b[l - 1]) l--;
        while (r + 1 < N && b[r] ^ b[r + 1]) r++;

        if (r - mid_p > mid_p - l) {
            return b[l];
        } else {
            return b[r];
        }
    }

    void solve() {
        input();
        int lb = 0;
        int ub = N;

        while (ub - lb > 1) {
            int mid = (lb + ub) / 2;
            if (check(mid)) {
                lb = mid;
            } else {
                ub = mid;
            }
        }

        cout << lb << endl;


    }
};


