int A[200000], B[200000];
int N;

int cal() {
	int a = N - 2;
	int b = N - 1;
	int c = N;
	if (B[a] == 1 && B[b] == 0 && B[c] == 1) {
		int r = 2;
		int s = 0;
		while (b - r >= 0) {
			if (B[b - r] == s && B[b + r] == s) r++;
			else return 1 - s;
		    s = 1 - s;
		}
		return B[b];
	}
	else if (B[a] == 0 && B[b] == 1 && B[c] == 0) {
		int r = 2;
		int s = 1;
		while (b - r >= 0) {
			if (B[b - r] == s && B[b + r] == s) r++;
			else return 1 - s;
		    s = 1 - s;
		}
		return B[b];
	}
	else {
		if (B[a] + B[b] + B[c] > 1) return 1;
		return 0;
	}
	return 0;
}

