    	int m = s + (b - s) / 2;
    	for (int i = 0;i < 2 * N - 1;i++) {
    		if (A[i] <= m) B[i] = 0;
    		else B[i] = 1;
    	}
    	if (cal() == 1) s = m;
    	else b = m;
    }
    cout << s << endl;
    return 0;
}

