bool chk(int x){
    for(int i=1;i<=2*N-1;i++){
        b[i] = (a[i] >= x);
    }
    int L = 0, R = 2*N;
    for(int i=N-1;i>=1;i--){
        if(b[i]==b[i+1]){
            L = i;
            break;
        }
    }
    for(int i=N+1;i<=2*N-1;i++){
        if(b[i]==b[i-1]){
            R = i;
            break;
        }
    }

    if(L==0&&R==2*N){
        return (N%2>0)?b[N]:!b[N];
    }else if(N-L < R-N){
        return b[L];
    }else{
        return b[R];
    }
}

