const int N = 100000 * 2 + 10;

int n;
int a[N];
int b[N];

bool check(int val){
    for (int i = 1; i <= 2 * n - 1; i++)
        b[i] = (a[i] >= val);

    bool flag = 1;
    for (int i = 2; i <= 2 * n - 1; i++)
        flag &= (b[i] != b[i - 1]);
    if (flag) // all flip bit
        return (n % 2 == 0 ? !b[n] : b[n] );

    int posL = 0, posR = 0;
    for (int i = n; i <= 2 * n - 1; i++)
        if (b[i] == b[i + 1]) {posL = i; break; }
    for (int i = n; i >= 1; i--)
        if (b[i] == b[i - 1]) {posR = i; break; }
    if (!posL) return b[posR];
    if (!posR) return b[posL];
    return (abs(posR - n) < abs(posL - n) ? b[posR] : b[posL]);
}

