	if(b > c) {
		int t = c;c = b;b = t;
	}
	return (a > b ? a : b);
}
int xchar() {
	static char ibuf[S];
	static int pos = 0,len = 0;
	if(pos == len) 
		pos = 0,len = fread(ibuf,1,S,stdin);
	if(pos == len) return EOF;
	return ibuf[pos++];
}
int read() {
	int c = xchar(),x = 0;
	while(!isdigit(c)) {
		c = xchar();
	}
	do
	{
		x = (x << 3) + (x << 1) + (c & 15);
		c = xchar();
	}while(isdigit(c));
	return x;
}
