    bool all = true;
    rep(i, 2 * n - 2) {
        d[i] = b[i] ^ b[i + 1];
        if (d[i] == 0)all = false;
    }
    //dump(b, 2 * n - 1);
    //dump(d, 2 * n - 2);
    if (all)return b[0] == 1;
    if ((b[n - 2] == 0 && b[n - 1] == 0) ||
            (b[n - 1] == 0 && b[n] == 0)) {
        return false;
    }
    if ((b[n - 2] == 1 && b[n - 1] == 1) ||
            (b[n - 1] == 1 && b[n] == 1)) {
        return true;
    }
    // 00, 11
    // 01
    // 0011
    // 00, 11
    li left = n, right = n;
    while (right < n * 2 - 2 && d[right] > 0)right++;
