	}
	cer.assign(ar.size(), 0);
	for (int i = 0; i < ar.size(); i++){
		if (ar[i]){
			if (i&&ar[i - 1]){
				cer[i] = ar[i];
			}
			if (i + 1 < ar.size() && ar[i + 1]){
				cer[i] = ar[i];
			}
		}
	}
	int u = n-1;
	if (cer[u]){
		return true;
	}
	int lef = 0;
	for (int i = 0; i < u; i++){
		if (cer[i]){
			lef = i + 1;
		}
	}
	int rig = ar.size() - 1;
	for (int i = ar.size() - 1; i > u; i--){
		if (cer[i]){
			rig = i-1;
		}
	}
	{
		int cnt = 0;
		int ich = rig-1;
		while (ar[ich]&&ich>=0){
			cnt++;
			ich -= 2;
		}
		if (ich < lef){
			return true;
		}
		int len = rig - ich+1;
		len /= 2;
		if (rig - lef + 1 <= u){
			return true;
		}
	}
	{
		int cnt = 0;
		int ich = lef+1;
		while (ar[ich] && ich < ar.size()){
			cnt++;
			ich += 2;
		}
		int len = ich - lef - 1;
		len /= 2;
		if (lef + len-1 >= u){
