const int N=1e6+5,inf=1e9;
int a[N],n;
bool cal(int md){
    int mn[2]={inf,inf};
    for(int i=0;i<n-1;i++){
        if((a[n+i]>=md)==(a[n+i+1]>=md))mn[a[n+i]>=md]=min(mn[a[n+i]>=md],i);
        if((a[n-i]>=md)==(a[n-i-1]>=md))mn[a[n-i]>=md]=min(mn[a[n-i]>=md],i);
    }
    return mn[0]^mn[1]?mn[1]<mn[0]:a[n]<md;
}
