}

const int MaxN = 100000;
const int MaxM = MaxN * 2 - 1;

int n, m;
int a[MaxM + 1];

inline int is_pillar(int l, int x)
{
	if (l > 1 && (a[l] > x) == (a[l - 1] > x))
		return a[l] > x;
	if (l < m && (a[l] > x) == (a[l + 1] > x))
		return a[l] > x;
	return -1;
}

inline bool check(int x)
{
	if (!is_pillar(n, x))
		return true;

	int pl = -1, vl = -1;
	for (int i = n; !~vl && i >= 1; --i)
		if (~(vl = is_pillar(i, x)))
			pl = i;

	int pr = -1, vr = -1;
	for (int i = n; !~vr && i <= m; ++i)
		if (~(vr = is_pillar(i, x)))
			pr = i;

	if (~vl && ~vr)
	{
		if (!vl && !vr)
			return true;

		int dl = n - pl, dr = pr - n;
		if (vl == 1)
			swap(dl, dr);
		if (dl < dr)
			return true;
	}
	else if (~vl)
	{
		if (!vl)
			return true;
	}
	else if (~vr)
	{
		if (!vr)
			return true;
	}
	else
	{
		int b = 0, w = 0;
		for (int i = 1; i <= m; ++i)
			b += a[i] <= x, w += a[i] > x;
		if (b > w)
			return true;
	}

	return false;
}

