bool good(int x)
{
	for (int i = 1; i < n; i ++)
	{
		if (b(x, n - i) == b(x, n - i - 1))
			return b(x, n - i);
		if (b(x, n + i) == b(x, n + i + 1))
			return b(x, n + i);
	}
	return b(x, n) ^ (n & 1 ^ 1);
}

int bs()
{
	int l = 0, r = 2 * n + 1;
	while (r - l > 1)
	{
		int mid = (r + l) / 2;
		if (good(mid))
			r = mid;
		else
			l = mid;
	}
	return r;
}

