// warning : there's get and sum method.
/// --- BIT Library {{{ ///

struct BIT{
  vector<long long> data;
  int n;
  BIT(int n): n(n){
    data.resize(n, 0);
  }
  void clear(int sz=0) {
    if(sz > 0) n = sz;
    data.assign(n, 0);
  }
  void add(int i, long long x) {
    i++;
    while(i<=n){
      data[i-1] += x;
      i += i&-i;
    }
  }
  void set(int i, long long x) {
    add(i, x - get(i));
  }
  long long sum(int i) {
    assert(i < n);
    if(i<0) return 0;
    i++;
    long long s = 0;
    while(i>0){
      s += data[i-1];
      i -= i&-i;
    }
    return s;
  }
  long long get(int i) {
    return sum(i) - (i ? sum(i - 1) : 0);
  }
  long long range(int a, int b) {
    return sum(b) - sum(a-1);
  }
};

/// }}}--- ///

const int N = 2e5;
BIT bit(N);
int n, m;
int a[N];
bool low[N];
bool is(int l, int r) {
  if(r < l) swap(l, r);
  if(r >= n) return 0;
  return bit.range(l + 1, r) == r - l;
}

void check(int i) {
  if(i < 0 || i >= n) return;
  bit.set(i, low[i] != low[i - 1]);
}

template<class T> inline int sign(T x) { return x > 0 ? 1 : x < 0 ? -1 : 0; }

