inline int read()
{
    re int res=0;re bool f=1;
    re char ch=getchar();
    while(ch<'0'||ch>'9')
    {
        if(ch=='-') f=0;
        ch=getchar();
    }
    while(ch>='0'&&ch<='9')
    {
        res=(res<<3)+(res<<1)+(ch&15);
        ch=getchar();
    }
    return f?res:-res;
}

inline int midnum(re int l,re int r,re int flag)
{
	re int firmax=0,secmax=0;

	if(firmax<num[l][flag])
	{
		secmax=firmax;
		firmax=num[l][flag];
	}
	else if(firmax==num[l][flag]) secmax=num[l][flag];
	else if(secmax<num[l][flag]) secmax=num[l][flag];

	if(firmax<num[l+1][flag])
	{
		secmax=firmax;
		firmax=num[l+1][flag];
	}
	else if(firmax==num[l+1][flag]) secmax=num[l+1][flag];
	else if(secmax<num[l+1][flag]) secmax=num[l+1][flag];

	if(firmax<num[r][flag])
	{
		secmax=firmax;
		firmax=num[r][flag];
	}
	else if(firmax==num[r][flag]) secmax=num[r][flag];
	else if(secmax<num[r][flag]) secmax=num[r][flag];

	return secmax;
}

