		{
			num[i][flag]=midnum(i-1,i+1,!flag);

			if(i+1>r) break;
			num[i+1][flag]=midnum(i,i+2,!flag);

			if(i+2>r) break;
			num[i+2][flag]=midnum(i+1,i+3,!flag);

			if(i+3>r) break;
			num[i+3][flag]=midnum(i+2,i+4,!flag);
