bool jud(int x)
{
	for(int i=1;i<2*n;i++)
	{
		if(a[i]<=x)b[i]=0;
		else b[i]=1;
	}
	if(b[n]==b[n-1]||b[n]==b[n+1])return b[n];
	bool flag=true;
	for(int i=1;i<2*n-1;i++)
	{
		if(b[i]==b[i+1])
		{
			flag=false;
			break;
		}
	}
	if(flag)
	{
		return b[n]^(n%2==0);
	}
	int l=0,r=n*2;
	for(int i=n-1;i;i--)
	{
		if(b[i]==b[i+1])
		{
			l=i;break;
