inline bool chk(int x) {
  int tot[2]; tot[0] = tot[1] = 0;
  for (int i = 1; i <= 2 * n; i++)
    if (a[i] >= x) b[i] = 1, tot[1]++;
    else b[i] = 0, tot[0]++;
  int pl = n - 1, pr = n + 1;
  while (pl >= 1 && pr <= n) {
    if (b[pl] == b[pl + 1]) {
      if (b[pl] == 0) return false;
      return true;
    }
    if (b[pr] == b[pr - 1]) {
      if (b[pr] == 0) return false;
      return true;
    }
  }
  if (tot[0] > tot[1]) return false;
  return true;
}

