void upd_limits(int n, int ind, int val, int &l, int &r){
	int l1 = ind, r1 = ind + 1, mid = n - 1;
	if (l1 > mid){
		r = min(r, l1);
	} else {
		l = max(l, r1);
	}
}

void upd_left_limit(int n, int val, int l, int r, int &l1){
	l1 = max(l1, l);
	for(; l1 <= r && per[l1] <= val; l1 += 2);
	l1 -= 2;
}

void upd_right_limit(int n, int val, int l, int r, int &r1){
	r1 = min(r1, r);
	for(; r1 >= l && per[r1] <= val; r1 -= 2);
	r1 += 2;
}

int solve_opt(int n){
	REPN(i, 1, 2 * n - 1){
		coord[i].clear();
	}

	REP(i, 0, 2 * n - 1){
		coord[per[i]].pb(i);
	}
	int mid = n - 1;
	for(int i = 1, l = 0, l1 = 0, r = 2 * n - 2, r1 = 2 * n - 2; ; i++){
		REP(j, 0, SZ(coord[i])){
			int ind = coord[i][j];
			if (ind < l || ind > r){
				continue;
			}
			if (ind > 0 && per[ind - 1] <= i){
				upd_limits(n, ind - 1, i, l, r);
			}
			if (ind < 2 * n - 2 && per[ind + 1] <= i){
				upd_limits(n, ind, i, l, r);
			}
		}
		upd_left_limit(n, i, l, r, l1);
		upd_right_limit(n, i, l, r, r1);
		if ((l + l1) / 2 >= mid || (r + r1) / 2 <= mid){
			return i;
		}
	}
}

