int check(int x) {
	memset(b, -1, sizeof(b));
	int cnt = 0;

	for (int i = 1; i <= n; i ++)
		if (a[i] < a[x]) b[i] = 0; else b[i] = 1;

	//for (int i = 1; i <= n; i ++)
	//	cout << b[i] << " ";
	//cout << endl;

	pre[++ cnt].l = pre[cnt].r = 0;
	for (int i = 1; i <= n; i ++) {
		int lst = i;
		while (lst <= n && b[lst] == b[lst + 1]) lst ++;
		if (lst - i >= 1)
			pre[++ cnt].l = i, pre[cnt].r = lst;
		i += (lst - i);
	}
	pre[++ cnt].l = pre[cnt].r = n + 1;

	//for (int i = 1; i <= cnt; i ++)
	//	cout << pre[i].l << " " << pre[i].r << endl;


	for (int i = 1; i <= cnt; i ++) {
		if (pre[i].l <= nn && nn <= pre[i].r)
			if (b[nn] == 1) return 1; else return 0;

		int l = pre[i - 1].r + 1, r = pre[i].l - 1;
		//cout << l << " " << r << endl;
		if (l > nn || nn > r || l > r) continue;
		int pos = l + (r - l + 1) / 2;
		if (pos >= nn)
			if (b[l] == 0) return 0; else return 1;
		else if (b[l] == 0) return 1; else return 0;
	}
}

