int N, A[202020]; bool v[202020];

bool chk(int m)
{
	for (int i=1;i<2*N;i++) v[i] = A[i] >= m;
	if (v[N] == A[N-1] || v[N] == A[N+1]) return v[N];

	int l = N, r = N;
	while (l > 1 && v[l-1] != v[l]) l--;
	while (r < 2*N-1 && v[r+1] != v[r]) r++;
	if ((l + r) >= N * 2) return v[l];
	return v[r];
}

int main()
{
	scanf("%d", &N);
	for (int i=1;i<2*N;i++) scanf("%d", &A[i]);

	int l = 1, r = 2*N-1;
	while (l + 1 < r){
		int m = (l + r) / 2;
		if (chk(m)) l = m;
		else r = m;
	}
	printf("%d\n", l);

	return 0;
}

