inline int med(int x, int y, int z) {
	if (x == y || y == z) {
		return y;
	} else if (x == z) {
		return x;
	} else if (y > x && y < z) {
		return y;
	} else if (y < x && y > z) {
		return y;
	} else if (x > y && x < z) {
		return x;
	} else if (x < y && x > z) {
		return x;
	} else {
		return z;
	}
}

