int main(int argc, char *argv[])
{
  int n;
  while (cin >> n) {
    vector<int> a(2 * n - 1);
    each (i, a) cin >> i;
    auto pred = [&] (int mid) {
      vector<bool> b;
      each (i, a) b.push_back(mid <= i);
      const int k = n - 1;
      int idx = -1;
      for (int i = 0; 0 <= k - i - 1; ++i) {
        if (b[k - i] == b[k - i - 1]) idx = k - i;
        if (b[k + i] == b[k + i + 1]) idx = k + i;
        if (idx != -1) break;
      }
      return idx == -1 ? b[0] : b[idx];
    };
    int small = 1;
    int large = n * 2;
    while (small + 1 < large) {
      int mid = (small + large) / 2;
      if (pred(mid)) small = mid;
      else large = mid;
    }
    if (pred(large)) cout << large << endl;
    else if (pred(small)) cout << small << endl;
    else assert(false);
  }
  return 0;
}

