bool lower(int n){
    rep(i,2*N-1){
        if (a[i]<=n) b[i]=1;
        else b[i]=0;
    }
    ll p=INF,q=INF;
    rep(i,2*N-2) if (b[i]==b[i+1]){
        if (b[i]==1){
            if (i<N-1) chmin(p,N-i-2);
            else chmin(p,i-N+1);
        }
        else {
            if (i<N-1) chmin(q,N-i-2);
            else chmin(q,i-N+1);
        }
    }
    if (p==INF&&q==INF) return b[0]==1;
    if (min(p,q)==p) return true;
    else return false;
}

