bool def(bool b[]){
	int s = 0, t = 0;
	while(N - s - 1> 0 && b[N - s - 1] != b[N - s - 2]) s++;
	while(t + N - 1 < 2 * N - 1 && b[N + t - 1] != b[N + t]) t++;
	if(min(s, t) >= N - 1) return ((N % 2) ? b[N - 1] : !b[N - 1]);
	else if(t < s) return b[t + N - 1];
	else return b[N - s - 1];
}

