using namespace std;

int N, p[200009], closest[200009];
bool x[200009], ap[200009];

bool atLeast (int K)
{
    for (int i=1; i<2 * N; i++)
        x[i] = (p[i] >= K), ap[i] = 0;
    bool atLeastOneStableSubarray = 0;
    for (int i=1; i<2 * N - 1; i++)
        if (x[i] == x[i + 1])
            ap[i] |= 1, ap[i + 1] |= 1, atLeastOneStableSubarray = 1;
    if (!atLeastOneStableSubarray) return x[1];
    if (ap[N]) return x[N];
    int lst = -2 * N;
    for (int i=1; i<2 * N; i++)
        if (ap[i]) lst = i;
        else closest[i] = lst;
    lst = 4 * N;
    for (int i=2 * N - 1; i>=1; i--)
        if (ap[i]) lst = i;
        else
        if (lst - i < i - closest[i]) closest[i] = lst;
    return x[closest[N]];
}

int binarySearch ()
{
    int p = 3, u = 2 * N - 2, ans = 2, mij;
    while (p <= u)
    {
        mij = (p + u) >> 1;
        if (atLeast (mij)) ans = mij, p = mij + 1;
        else u = mij - 1;
    }
    return ans;
}

