/*!* [binsearch] *!*/
/*+*  *+*/
// judge (l,r]
// l:[X] 
// r:[X] 
// judge:[F = X -> bool]  X 
// lower:[bool] false [l,r)
// eps:[X] 

template <typename X, typename F>
X BinSearch(X l, X r, F judge, bool lower = true, X eps = 1) {
    while (r - l > eps) {
        X m = (r + l) / 2;
        if (judge(m) == lower) {
            r = m;
        } else {
            l = m;
        }
    }
    return lower ? r : l;
}

int med(int a, int b, int c) {
    vector<int> v{a, b, c};
    sort(WHOLE(v));
    return v[1];
}

